import { useState } from "react";

const EditPassword = () => {

    const [oldPassword, setPasswordShown] = useState(false);
    // const [newPassword, setPasswordShown] = useState(false);

    // Password toggle handler
    const togglePassword = () => {
        // when the handler is invoked
        // inverse the boolean state of passwordShown
        setPasswordShown(!oldPassword);
    }

    return (
        <div className="hidden" id="passwordDisplay">
            <h2>Password</h2>
            <div id="currentPassword">Current Password<br></br><br></br>
                <input type={oldPassword ? "text" : "password"} id="fcurrentpassword" name="fcurrentpassword"></input>
                <button onClick={togglePassword}>Show Password</button><br></br><br></br>
            </div>
            <div id="newPassword">New Password<br></br><br></br>
                <input type={oldPassword ? "text" : "password"} id="fnewpassword" name="fnewpassword"></input>
                <button onClick={togglePassword}>Show Password</button><br></br><br></br>
            </div>
            <button type="submit" value="Submit">Save</button>
        </div>
    );
};

export default EditPassword;
