import { useState } from "react";

const SearchBox = () => {

    const [message, setMessage] = useState("");
    const [updated, setUpdated] = useState("");

    const handleSearch = (event) => {
        if (event.key === "Enter") {
            // get input value
            setUpdated(message)
        }
    };

    return (
        <div id="searchContainer">
            <div id="searchDiv">

                <div className="search-box">
                    <div className="search-icon"><i className="iconfont icon-sousuo" style={{ fontSize: "40px" }}></i></div>
                    <div id='search-border'>
                        <form action="" className="search-form">
                            <input type="text" placeholder="Search new place ..." id="search" ref={inputRef} onKeyDown={handleSearch}/>
                        </form>
                    </div>
                    <div className="go-icon"><i className="fa fa-arrow-right"></i></div>
                </div>

                <div className="newSearchBox">
                    <input type="text" />
                </div>

            </div>
        </div>
    );
};

export default SearchBox;
