import { useState } from "react";

const SearchBox = () => {

    const [message, setMessage] = useState("");
    const [updated, setUpdated] = useState("");

    const handleChange = (event) => {
        setMessage(event.currentTarget.value);
    };

    const handleSearch = (event) => {
        if (event.key === "Enter") {
            // get input value
            document.getElementById("search").value="";
            setUpdated(message);
            // console.log(event.target.reset());
        }
    };

    return (
        <div id="searchContainer">
            <div id="searchDiv">

                <div className="search-box">
                    <div className="search-icon"><i className="iconfont icon-sousuo" style={{ fontSize: "40px" }}></i></div>
                    <div id='search-border'>
                        {/* <form action="" className="search-form">
                            <input type="text" placeholder="Search new place ..." id="search" name="message" value={message} onChange={handleChange} onKeyDown={handleSearch}/>
                        </form> */}
                        <input type="text" placeholder="Search new place ..." id="search" name="message" value={message} onChange={handleChange} onKeyDown={handleSearch}/>
                    </div>
                    {/* <div className="go-icon"><i className="fa fa-arrow-right"></i></div> */}
                </div>
                <p >{updated}</p>
            </div>
        </div>
    );
};

export default SearchBox;
