import { BrowserRouter as Router, Routes, Route } from "react-router-dom"

// pages & component
import Home from "./pages/Home";
import AccountSettings from "./pages/AccountSettings";
import ErrorPage from "./pages/ErrorPage";
import Login from "./pages/Login";
import UseAxios from "./pages/Hooks/UseAxios";
import { createContext } from "react";

export const ImageContext = createContext();

function App() {

  const { response, isLoading, error, fetchData } = UseAxios(`search/photos?page=1&query=office&client_id=${process.env.REACT_APP_ACCESS_KEY}`);
  console.log(response);

  const value = {
    response,
    isLoading,
    error,
    fetchData
  }

  return (
    <Router>
      <Routes>
        <ImageContext.Provider value={value}>
          <Route path="/" element={<Home />} ></Route>
        </ImageContext.Provider>
        <Route path="/accountsettings" element={<AccountSettings />} ></Route>
        <Route path="/login" element={<Login />}></Route>
        <Route path="*" element={<ErrorPage />} ></Route>
      </Routes>
    </Router>
  );
}

export default App;
